import styled, { keyframes } from "styled-components"

export const Container = styled.div`
    height:100vh;
    display:flex;
    align-items: center;
    justify-content: center;
    text-align: center;
    flex-direction: column;
    h1{
        text-align:left;
        margin-left: 6px;
    }
`
const appearFromRight = keyframes`
    from{
        opacity:0;
        width:100%;
        transform: translateY(-50px)
    }
    to{
        opacity: 1;
        transform: translateY(0px)
    }

`
export const AnimationContainer = styled.div`
    display: flex;
    flex-direction:column;
    align-items: center;
    justify-content:center;
    animation: ${appearFromRight} 1s;
    form{
        width:160%;
        text-align:center;
        max-width: 320px;
    }
    h3{
        text-align: left;
    }
    p{
        margin-left:4px;
        margin-top:8px;
        text-align: left;
        a{
            font-weight: bold;
            color: var(--orange);
        }
    }
    @media(min-width: 800px) {
        form{
            margin:80px;
            width:540px;
            text-align:center;
        }
    }
`
export const Button = styled.button`
  border: 2px #0a315d;
  width: ${({ size }) => (size)};
  padding: 1.6vh;
  color: white;
  background-color: #0a315d;
  font-size: 1.1rem;
  border-radius: 3px;
  margin: 0 auto;
  margin-top: 2vh;
  text-align: center;
  font-family: "Londrina Solid", cursive;
`;
