import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  position: relative;

  figure {
    height: 85vh;
    width: 100%;
    img {
      height: 85vh;
      width: 100%;
      object-fit: cover;
    }
  }
  .logo {
    z-index: 2;
    position: absolute;
    top: 3vh;
    left: 50%;
    transform: translateX(-50%);
  }
  .containerButtons {
    display: flex;
    margin-top: 3.5vh;
    column-gap: 5%;
    justify-content: center;
  }

  .bar {
    margin: 0 auto;
    height: 4px;
    width: 200px;
    background-color: black;
    margin-top: 2vh;
  }
`;
export const Button = styled.button`
  border: 2px #0a315d;
  width: ${({ size }) => (size)};
  padding: 1.6vh;
  color: white;
  margin-top: 1vh;
  background-color: #0a315d;
  font-size: 1.1rem;
  border-radius: 2px;
  margin: 0 auto;
  text-align: center;
  font-family: "Londrina Solid", cursive;
  @media(max-width: 400px) {
        margin-top: ${({ margin }) => (margin)};
    }
`;
